
using Microsoft.Extensions.Configuration;
using Microsoft.VisualStudio.TestTools.UnitTesting;

using System;
using System.Transactions;
namespace LicenseManager.TestAPIs
{
    [TestClass]
    public class BaseTest
    {
        static BaseTest()
        {

        }

        [TestInitialize]
        public virtual void Init()
        {

            // Inizializzare EF per SQL Server
            //EVLogCore.Entity.SqlClient.BootStrapper.Initialize();
        }

        protected static string TokenAccess { get => $""; }

        protected static void DoSQLTestThrowException(
            Action action
        )
        {
            using TransactionScope transactionScope = new();
            try
            {
                action();
            }
            catch (Exception ex)
            {
                //ok = false;
                Console.WriteLine(ex.Message);
                throw;
            }
            finally
            {
                transactionScope.Dispose();
            }
        }


    }
